import React, { Component } from "react";
import axios from "axios";

class StravaExport extends Component {
  state = {
    client_id: "47805",
    client_secret: "aeb849953d794088bb82fbce08b6e12588fa7725",
    authCode: "",
    refresh_token: "",
  };

  componentDidMount() {
    const search = this.props.location.search;
    const params = new URLSearchParams(search);
    this.setState({ authCode: params.get("code") });

    axios
      .post("https://www.strava.com/oauth/token", {
        client_id: this.state.client_id,
        client_secret: this.state.client_secret,
        code: this.state.authCode,
        grant_type: "authorization_code",
      })
      .then((response) => {
        this.setState({ refresh_token: response.refresh_token });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return <div>{this.state.authCode}</div>;
  }
}

export default StravaExport;
