import React, { Component } from "react";
import getRefreshToken from "../utils/getRefreshToken";
import getActivities from "../utils/getActivities";
import { response } from "express";

class StravaExport extends Component {
  state = {
    client_id: "47805",
    client_secret: "aeb849953d794088bb82fbce08b6e12588fa7725",
    authCode: "",
    refresh_token: "",
    activities: [],
  };

  componentDidMount() {
    const search = this.props.location.search;
    const params = new URLSearchParams(search);
    const authCode = params.get("code");

    console.log(this.state.client_id, this.state.client_secret, authCode);

    getRefreshToken(this.state.client_id, this.state.client_secret, authCode)
      .then((response) => {
        getActivities(this.state.client_id, this.state.client_secret, response);
        //this.setState({ refresh_token: response });
      })
      .then((response) => {
        this.setState({ activities: response });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div>
        {this.state.authCode}
        <p>{this.state.refresh_token}</p>
      </div>
    );
  }
}

export default StravaExport;
