import getActivities from "./getActivities"

async function downloadJSON(client_id, client_secret, refresh_token, selectedIndex, filename) {
    const activities = await getActivities(client_id, client_secret, refresh_token, false)
    console.log(activities)
    let activitiesJSON = []
    for (let i of selectedIndex) {
        const activity = JSON.stringify(activities[i])
        activitiesJSON.push(activity)
    }
    console.log(activitiesJSON)

    const blob = new Blob(activitiesJSON, {type: "application/json"})
    const url = URL.createObjectURL(blob)
    var a = document.createElement('a');
    a.href = url;
    a.download = filename;
    return a;
    

}

export default downloadJSON;
