import axios from "axios";

//use for manual entry and use in a loop for Nike conversion to convert all runs selected



function createActivity(client_id, client_secret, refresh_token, activity) {
    return axios({
        method: "post",
        url: "http://localhost:8000/api/strava/createActivities",
        data: {
          client_id: client_id,
          client_secret: client_secret,
          refresh_token: refresh_token,
          name: activity.name,
          type: activity.type,
          start_date_local: activity.start_date_local,
          elapsed_time: activity.moving_time,
          moving_time: activity.moving_time,
          description: activity.description,
          distance: activity.distance
        },
      }).then((response) => {
        return response.data;
      });
}

export default createActivity;
